import React from 'react'
import './Chart.css'
import ChartBar from './ChartBar'
const Chart = (props) => {
    const dataPointValue = props.dataPoints.map(dataPoint => dataPoint.value);
    const totalValue = Math.max(...dataPointValue);
    return (
        <div className="chart">
           {props.dataPoints.map(dataPoint => <ChartBar key={dataPoint.label} value={dataPoint.value} maxValue={totalValue} label={dataPoint.label} /> )} 
        </div>
    )
}

export default Chart
