import React, { useState } from 'react'
import './Expense.css'
import ExpenseItem from './ExpenseItem'
import Card from '../Ui/Card'
import ExpensesFilter from './ExpensesFilter'
const Expense = (props) => {
    const [filterYear, setFilterYear] = useState('2021');
    const chengeFilter = (selectedYear) => {
        setFilterYear(selectedYear);
    }
    const filteredYear = props.items.filter(expense => expense.date.getFullYear().toString() === filterYear);
    return (
        <div>

            <Card className="expenses">
                <ExpensesFilter selected={filterYear} onChangeFilter={chengeFilter} />
                {filteredYear.map((filteredExpense) => (<ExpenseItem key={expense.id} title={fxpense.title} amount={filteredExpense.amount} date={filteredExpense.date} />))}
              
            </Card>
        </div>
    )


}

export default Expense
